        public object GetCategories()
        {
            return _categories;
        }

        public object GetProducts(int categoryId)
        {
            var result = from p in _products
                         where p.CategoryId == categoryId
                         select p;

            return result.ToList();
        }

        public void DeleteProduct(BindingSource bindingSource, int productId)
        {
            var query = from p in _products
                        where p.ProductID == productId
                        select p;
            var product = query.Single();

            bindingSource.Remove(product);
        }

        public void AddProduct(BindingSource bindingSource, Product product)
        {
            var maxId = (
                from p in _products
                select p).Max(p => p.ProductID);
            product.ProductID = maxId + 1;

            bindingSource.Add(product);
        }

        public void Save()
        {
            //Nothing to do
        }
    }
}

// 1] Declare delegate -- defines required signature:=================
    delegate double MathAction_Delegate(double num);

    class DelegateTest
        {
// 2] Regular method that matches signature:=================
        static double method_Double(double input)
            {
            return input * 2;
            }

        static void Main()
            {
// 3] Instantiate delegate with named method:
            MathAction_Delegate ma_InstatiantiatedDelegate = method_Double;

// 4a] Either Invoke delegate ma_InstatiantiatedDelegate:
            double multByTwo = ma_InstatiantiatedDelegate(4.5);
            Console.WriteLine("multByTwo: {0}", multByTwo);

// or 4b] Instantiate delegate with anonymous method:
            MathAction_Delegate ma_InstatiantiatedDelegate2 = delegate(double input)
            {
                return input * input;
            };

            double square = ma_InstatiantiatedDelegate2(5);
            Console.WriteLine("square: {0}", square);

// or 4c] Instantiate delegate with lambda expression
            MathAction_Delegate ma_InstatiantiatedDelegate3 = s => s * s * s;
            double cube = ma_InstatiantiatedDelegate3(4.375);

            Console.WriteLine("cube: {0}", cube);
            }
        }
        // Output:
        // multByTwo: 9
        // square: 25
        // cube: 83.740234375

