//Passing a Generic Interface as Argument

//A generic interface is primarily a normal interface like any other. 
//        It can be used to declare a variable but assigned the appropriate class. 
//    It can be returned from a method. It can be passed as argument.

//You pass a generic interface primarily the same way you would an interface. 
//    In the body of the method, you can ignore the argument or use it any way appropriate. 
//        For example, you can access its members. Here is an example:

using System;

public interface IShapes<T>				//<=== 1] Create the base GENERIC interface
{
    int Count { get; }
    void Add(T item);
    T Get(int index);
}

public class GeometricShapes<T> : IShapes<T>	//<=== 2] Derive a second (derived) GENERIC Interface from the first GENERIC Interface
{
    private int size;
    private T[] items;

    //Constructor
	public GeometricShapes()
    {
        size = 0;
        items = new T[10];
    }

	//Contract Property
    public int Count { get { return size; } }

	//Contract Property
	public void Add(T item)
    {
        this.items[this.size] = item;
        this.size++;
    }

	//Contract Property
	public T Get(int index) { return this.items[index]; }
}

public interface IRound			//<=== 3] Create a regular interface
{
    string Name { get; }
    double Radius { get; set; }
    double Diameter { get; }
    double Circumference { get; }
    double Area { get; }
}

public class Circle : IRound	//<==	4] Derive a regular class from the regular Interface
{
    protected double rad;
    protected string id;

    //Constructor
	public Circle(double radius = 0.00D)
    {
        this.rad = radius;
    }

	//Contract Property
	public string Name { get { return "Circle"; } }

	//Contract Property
	public double Radius
    {
        get { return rad; }
        set
        {
            if (rad <= 0) rad = 0;
            else rad = value;
        }
    }

	//Contract Property
	public double Diameter { get { return rad * 2; } }

	//Contract Property
	public double Circumference { get { return rad * 2 * 3.14159; } }

	//Contract Property
	public double Area { get { return rad * rad * 3.14159; } }
}

public class Exercise
{
    public Circle GetShape()	//Method that returns a regular classs
    {
        double rad = 0.00D;

        Console.Write("Enter the radius: ");
        rad = double.Parse(Console.ReadLine());

        return new Circle(rad);	//Instantiate the regular class, and return it.
    }

    public void ShowShapes(IShapes<IRound> shps)	//A void method that takes a Parm that has the base Generic Interface
    {
        for (int i = 0; i < shps.Count; i++)
        {
            IRound rnd = shps.Get(i);

            Console.WriteLine("================================");
            Console.WriteLine("{0} Characteristics", rnd.Name);
            Console.WriteLine("--------------------------------");
            Console.WriteLine("Radius:        {0}", rnd.Radius);
            Console.WriteLine("Diameter:      {0}", rnd.Diameter);
            Console.WriteLine("Circumference: {0}", rnd.Circumference);
            Console.WriteLine("Area:          {0}", rnd.Area);
        }
        Console.WriteLine("===============================");
    }

    public static int Main()
    {
        Exercise exo = new Exercise();										//Not sure of this, but it is instantiating itself
        GeometricShapes<IRound> shapes = new GeometricShapes<IRound>();		//Providing a regular Interface as the 'T' type for a Generic Interface.

        IRound rnd = exo.GetShape();				//Call this Class's method to populate the regular Class with a circle instance (which is a regular class)	
		shapes.Add(rnd);							// Now add the circle to the 'collection' of type <IRound>
        rnd = exo.GetShape();
        shapes.Add(rnd);
        rnd = exo.GetShape();
        shapes.Add(rnd);
        rnd = exo.GetShape();
        shapes.Add(rnd);
        rnd = exo.GetShape();
        shapes.Add(rnd);

        Console.Clear();
        exo.ShowShapes(shapes);

        return 0;
    }
}

