============ /Extensions/HtmlExtensions.cs ========================

using System;
using System.Linq.Expressions;
using System.Web.Mvc;

//namespace System.Web.Mvc.Html
namespace WA007_MVC_MileageReimbursement.Extensions
	{
	public static class HtmlExtensions
		{
		//public static MvcHtmlString WorkFlowStepFormatted<TModel,TValue>(this HtmlHelper<TModel> helper,Expression<Func<TModel,TValue>> expression)
		public static MvcHtmlString WorkFlowStepFormatted<TModel,TValue>(this HtmlHelper<TModel> htmlHelper,Expression<Func<TModel,TValue>> expression,object htmlAttributes = null)
			{

			string sx1_prefix = "<div class='";
			string sx1_middle = "";				//  <== This is built in the switch/case loop
			string sx1_suffix = "'>";
			//string sx2_prefix = "@Html.DisplayFor(";
			////////////////////////////////////////////////////////////////////////////////
			var value = ModelMetadata.FromLambdaExpression(expression, htmlHelper.ViewData).Model.ToString();
			////////////////////////////////////////////////////////////////////////////////
			//string sx2_suffix = ")";
			string sx3 = "</div>";

			switch (value)
				{
				case "UnSubmittedRecord":
					sx1_middle = "text-primary";
					break;
				case "RequestFromEmployeeSubmitted":
					sx1_middle = "text-primary";
					break;
				case "ApprovedBySupervisor":
					sx1_middle = "text-gigMileageGreen";
					break;
				case "RejectedBySupervisor":
					sx1_middle = "text-danger";
					break;
				case "ApprovedByAccounting":
					sx1_middle = "text-gigMileageGreen";
					break;
				case "RejectedByAccounting":
					sx1_middle = "text-danger";
					break;
				case "ApprovedByCEO":
					sx1_middle = "text-gigMileageGreen";
					break;
				case "RejectedByCEO":
					sx1_middle = "text-danger";
					break;
				default:
					sx1_middle = "text-primary";
					break;
				}

			MvcHtmlString MvcHtmlString_out = new MvcHtmlString(sx1_prefix + sx1_middle + sx1_suffix + value + sx3);

			return 	MvcHtmlString_out;


			}


		}
	}
