using System.Configuration; //For Connection String from web.config
using System.Data.SqlClient;    //For SqlConnection
using System.Data; //for: CommandType 	


namespace WA0005_NewHire
{

    public static class Specific_SQL
    {

        public static void InsertNoteTo_T00_WriteUsageLog_ForWebFrontEnds(string sNote)
        {

            try
            {

                {

                    using (SqlConnection conn = new SqlConnection(ConfigurationManager.ConnectionStrings["DB2_CRMConnectionString1"].ConnectionString))
                    {
                        using (SqlCommand cmd = new SqlCommand("INSERT INTO T00_WriteUsageLog_ForWebFrontEnds (Note) VALUES (@Note)"))
                        {
                            cmd.CommandType = CommandType.Text;
                            cmd.Parameters.Add(new SqlParameter("@Note", sNote));

                            if (conn.State != ConnectionState.Open)
                            {
                                conn.Open();
                            }
                            cmd.Connection = conn;

                            cmd.ExecuteNonQuery();
                            conn.Close();   //This may not be necessary
                        }
                    }

                }


            }

            catch (System.Data.SqlClient.SqlException ex)
            {

                Debug.Print("SqlException in InsertNoteTo_T00_WriteUsageLog_ForWebFrontEnds" + ex.Message);

            }
            catch (Exception e)
            {
                Debug.WriteLine("InsertNoteTo_T00_WriteUsageLog_ForWebFrontEnds - Exception!" + e.ToString() + gigGlobal.vbCrLf + e.Message);
            }


        }

     }

  }

