If($TrialRun){$Subject += " Trial Run"} Function SendEmailStatus($From, $To, $Subject, $SMTPServer, $BodyAsHtml, $Body) { $SMTPMessage = New-Object System.Net.Mail.MailMessage $From, $To, $Subject, $Body $SMTPMessage.IsBodyHTML = $BodyAsHtml $SMTPClient = New-Object Net.Mail.SMTPClient($SMTPServer) $SMTPClient.Send($SMTPMessage) If($? -eq $False){Write-Warning "$($Error[0].Exception.Message) | $($Error[0].Exception.GetBaseException().Message)"} $SMTPMessage.Dispose() rv SMTPClient rv SMTPMessage }
Function Write-toFile{ <# .SYNOPSIS Writing information to file .DESCRIPTION Function to write information to file #> Param ([string]$WriteLine) Out-File $XMLFile -encoding utf8 -input $WriteLine -append Write-Host $WriteLine }
Function Write-ToLog([string]$message, [string]$file) { <# .SYNOPSIS Writing log to the logfile .DESCRIPTION Function to write logging to a logfile. This should be done in the End phase of the script. #> If(-not($file)){$file=$LogFile} $Date = $(get-date -uformat %Y-%m-%d-%H.%M.%S) $message = "$Date | `t$message" Write-Verbose $message Write-Host $message #Write Log to log file Without ASCII not able to read with tracer. Out-File $file -encoding ASCII -input $message -append }
Function GetSuperSededList {
$Script:countAllUpdates = 0 $Script:countSupersededAll = 0 $Script:countSupersededLastLevel = 0 $Script:countSupersededExclusionPeriod = 0 $Script:countSupersededLastLevelExclusionPeriod = 0 $Script:countDeclined = 0
$Prop = [ordered]@{} $ErrorActionPreference = "Stop" foreach($update in $allUpdates) {
$Script:countAllUpdates++
if ($update.IsDeclined) { $Script:countDeclined++ }
if (!$update.IsDeclined -and $update.IsSuperseded) { $Script:countSupersededAll++
if (!$update.HasSupersededUpdates) { $Script:countSupersededLastLevel++ } ################### if ($update.CreationDate -lt (get-date).AddDays(-$ExclusionPeriod)) { #"$($update.Id.UpdateId.Guid), $($update.Id.RevisionNumber), $($update.Title), $($update.KnowledgeBaseArticles), $($update.SecurityBulletins), $($update.HasSupersededUpdates)" | Out-File $outSupersededExList -Append "$($update.Title), $($update.KnowledgeBaseArticles), $($update.ArrivalDate), $($update.SecurityBulletins), $($update.UpdateClassificationTitle), $($update.ProductTitles), $($update.HasSupersededUpdates)" | Out-File $outSupersededExList -Append $Script:countSupersededExclusionPeriod++ if (!$update.HasSupersededUpdates) { $Script:countSupersededLastLevelExclusionPeriod++ } }
"$($update.Id.UpdateId.Guid), $($update.Id.RevisionNumber), $($update.Title), $($update.KnowledgeBaseArticles), $($update.ArrivalDate), $($update.SecurityBulletins), $($update.HasSupersededUpdates)" | Out-File $outSupersededList -Append
$Prop.Title = [string]$update.Title $Prop."KB Article" = [string]$update.KnowledgeBaseArticles $Prop."Arrival Date" = [string]$update.ArrivalDate $Prop.Classification = [string]$update.UpdateClassificationTitle $Prop."Product Title" = [string]$update.ProductTitles $Prop."Has Superseded Updates" = [string]$update.HasSupersededUpdates
New-Object PSObject -property $Prop } }
}
Function Decline-Superseded{ #Write-ToLog ""
Write-ToLog "" Write-ToLog "$WsusServer is starting Decline-SupersededUpdates function..."
"UpdateID, RevisionNumber, Title, KBArticle, ArrivalDate, SecurityBulletin, LastLevel" | Out-File $outSupersededList "Title, KBArticle, ArrivalDate, SecurityBulletin, UpdateClassificationTitle, ProductTitles, HasSupersededUpdates" | Out-File $outSupersededExList
##########################
GetSuperSededList | ConvertTo-HTML -head $CStyle | Out-File $outSupersededHTM
Write-ToLog "Done." if ($csv){Write-ToLog "List of superseded updates: $outSupersededList"} Write-ToLog "List of Superseded Updates: $outSupersededHTM"
Write-ToLog "" Write-ToLog "Superseded Summary:" Write-ToLog "========" Write-ToLog "All Updates = $countAllUpdates" Write-ToLog "Any except Declined = ($countAllUpdates - $countDeclined)" Write-ToLog "All Superseded Updates = $countSupersededAll" Write-ToLog " Superseded Updates (Intermediate) = ($countSupersededAll - $countSupersededLastLevel)" Write-ToLog " Superseded Updates (Last Level) = $countSupersededLastLevel" Write-ToLog " Superseded Updates (Older than $ExclusionPeriod days) = $countSupersededExclusionPeriod" Write-ToLog " Superseded Updates (Last Level Older than $ExclusionPeriod days) = $countSupersededLastLevelExclusionPeriod" Write-ToLog ""
$i = 0 if (!$TrialRun) {
Write-ToLog "TrialRun flag is set to $TrialRun. Continuing with declining updates" $updatesDeclined = 0
if ($DeclineLastLevelOnly) { Write-ToLog " DeclineLastLevel is set to True. Only declining last level superseded updates."
foreach ($update in $allUpdates) {
if (!$update.IsDeclined -and $update.IsSuperseded -and !$update.HasSupersededUpdates) { if ($update.CreationDate -lt (get-date).AddDays(-$ExclusionPeriod)) { $i++ $percentComplete = "{0:N2}" -f (($updatesDeclined/$countSupersededLastLevelExclusionPeriod) * 100) Write-Progress -Activity "Declining Updates" -Status "Declining update #$i/$countSupersededLastLevelExclusionPeriod - $($update.Id.UpdateId.Guid)" -PercentComplete $percentComplete -CurrentOperation "$($percentComplete)% complete"
try { $update.Decline() $updatesDeclined++ } catch [System.Exception] { Write-ToLog "$WsusServer failed to decline update $($update.Id.UpdateId.Guid). Error:" $_.Exception.Message Write-EventLog -LogName $Eventlog -EventID 21031 -Message "$WsusServer failed to decline update $($update.Id.UpdateId.Guid). Error: $error[0]" -Source $EventSource -EntryType Error } } } } } else { Write-ToLog " DeclineLastLevel is set to False. Declining all superseded updates."
foreach ($update in $allUpdates) {
if (!$update.IsDeclined -and $update.IsSuperseded) { if ($update.CreationDate -lt (get-date).AddDays(-$ExclusionPeriod)) {
$i++ $percentComplete = "{0:N2}" -f (($updatesDeclined/$countSupersededAll) * 100) Write-Progress -Activity "Declining Updates" -Status "Declining update #$i/$countSupersededAll - $($update.Id.UpdateId.Guid)" -PercentComplete $percentComplete -CurrentOperation "$($percentComplete)% complete" try { $update.Decline() $updatesDeclined++ } catch [System.Exception] { Write-Host "$WsusServer failed to decline update $($update.Id.UpdateId.Guid). Error:" $_.Exception.Message Write-EventLog -LogName $Eventlog -EventID 21031 -Message "$WsusServer failed to decline update $($update.Id.UpdateId.Guid). Error: $error[0]" -Source $EventSource -EntryType Error } } } }
}
Write-ToLog " Declined $updatesDeclined updates." #if ($updatesDeclined -ne 0) { # Copy-Item -Path $outSupersededList -Destination $outSupersededListBackup -Force # Write-ToLog " Backed up list of superseded updates to $outSupersededListBackup" #}
} else { Write-ToLog "TrialRun flag is set to $TrialRun. Skipped declining updates"
if(Test-Path $outSupersededExList){ Import-csv $outSupersededExList | ConvertTo-HTML -head $table | Out-File $outSupersededExHTM}
}
}
Function Decline-Itanium{
Write-ToLog "$WsusServer is starting Decline-WsusItaniumUpdates function..... Please wait...." #Write-ToLog "Connecting to $WSUSserver..."
if ($TrialRun) {Write-ToLog "NOTE: TrialRun flag is set to $TrialRun. Recording all Itanium updates..."}Else{Write-ToLog "NOTE: TrialRun flag is set to $TrialRun. Continuing with declining updates..."}
Write-ToLog "Searching for Itanium updates..." #$ItaniumUpdates = $WsusServerAdminProxy.GetUpdates() | ?{-not $_.IsDeclined -and $_.Title -match ia64|itanium}
$ItaniumUpdates = $GrabUpdates | where-object {$_.Title -match "ia64|itanium"} $script:Itancount = $ItaniumUpdates.count
If($ItaniumUpdates) { Write-ToLog "Found $script:Itancount Itanium Updates..." Try { If(!$TrialRun){Write-ToLog "Declining Itanium Updates...";$ItaniumUpdates | %{$_.Decline()}}Else{Write-ToLog "Recording Itanium Updates..."} $Table = @{Name="Title";Expression={[string]$_.Title}},` @{Name="KB Article";Expression={[string]$_.KnowledgebaseArticles}},` @{Name="Arrival Date";Expression={[string]$_.ArrivalDate}},` @{Name="Classification";Expression={[string]$_.UpdateClassificationTitle}},` @{Name="Product Title";Expression={[string]$_.ProductTitles}},` @{Name="Product Family";Expression={[string]$_.ProductFamilyTitles}} #$ItaniumUpdates | Select $Table $ItaniumUpdates | Select $Table | ConvertTo-HTML -head $CStyle | Out-File $IThtmfile If(!$TrialRun){Write-ToLog "List of Itanium updates declined: $IThtmfile"; Write-ToLog ""} Else{Write-ToLog "List of Itanium updates that could be declined: $IThtmfile"; Write-ToLog ""}
} Catch { Write-EventLog -LogName $Eventlog -EventID 21031 -Message "$WsusServer is unable to Decline Itanium Updates. Error: $error[0]" -Source $EventSource -EntryType Error Write-ToLog "$WsusServer is unable to Decline Itanium Updates. Error: $error[0]" } } Else { Write-ToLog "" Write-ToLog " No Itanium Updates found that needed declining at this time..." Write-ToLog "" } $ErrorActionPreference = $script:CurrentErrorActionPreference
}
