}
Function UpdateListMaint{
Write-ToLog "CleanUpdatelist is set to $CleanUpdatelist. Cleaning the UpdateList folder." Write-ToLog "Deleting $CleanULNumber days old files and containers." Try{ get-childitem -Path $ulpath -recurse | where-object {$_.lastwritetime -lt (get-date).addDays(-$CleanULNumber)} | Foreach-Object { remove-item $_.FullName -force -recurse} get-ChildItem $ulpath -recurse | Where-Object {$_.PSIsContainer -eq $True} | Where-Object {$_.GetFiles().Count -eq 0} | Foreach-Object { remove-item $_.FullName -recurse} Write-ToLog "Successfully removed files that are older than $CleanULNumber." } Catch { Write-ToLog "Failed to clean the UpdateList files/folder. Error: $Error[0]" Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Failed to clean the UpdateList files/folder. Error: $Error[0]" -Source $EventSource -EntryType Error Write-ToLog "Please check the files manually." If($EmailReport) { $Body = ConvertTo-Html -head $CStyle -Body "Failed to clean the UpdateList files/folder. Error: $Error[0]. Please check the files manually." | Out-String $Body = $Body.Replace("<table>`r`n</table>", "") SendEmailStatus -From $From -To $To -Subject $Subject -SmtpServer $SmtpServer -BodyAsHtml $True -Body $Body Write-ToLog "Sending Mail..." }
}
}
