#thePythonScriptFile.py import pandas as pd
def compute_tau(df_SQL_IN: pd.DataFrame, parmMultiplier_IN: int) -> (pd.DataFrame, float): df_PYTHON_OUT = pd.DataFrame( [parmMultiplier_IN * df_SQL_IN["pi"][0]], columns=["colTau"]) return df_PYTHON_OUT, float(df_PYTHON_OUT["colTau"][0])
def test_compute_tau(): df_SQL_IN = pd.DataFrame([3.14159], columns=['pi']) df, colTau = compute_tau(df_SQL_IN, 2) print('DataFrame:\n', df) print('tau = ', colTau)
if __name__ == '__main__': test_compute_tau()
