--compute_tau_module_v2.sql
--========================================compute_tau.py ==> in path: C:\temp\ ===============START --#thePythonScriptFile.py --import pandas as pd
--def compute_tau(df_SQL_IN: pd.DataFrame, parmMultiplier_IN: int) -> (pd.DataFrame, float): -- df_PYTHON_OUT = pd.DataFrame( -- [parmMultiplier_IN * df_SQL_IN["pi"][0]], columns=["colTau"]) -- return df_PYTHON_OUT, float(df_PYTHON_OUT["colTau"][0])
--def test_compute_tau(): -- df_SQL_IN = pd.DataFrame([3.14159], columns=['pi']) -- df, colTau = compute_tau(df_SQL_IN, 2) -- print('DataFrame:\n', df) -- print('tau = ', colTau)
--if __name__ == '__main__': -- test_compute_tau() --========================================compute_tau.py===================END /* This will fail until the following is done (https://stackoverflow.com/questions/64530962/importing-python-scripts-in-sql-server-stored-procedure)
1] Assign a folder for the Python Modules to be imported and used by the SQL scripts 2] Set permissions on the folder: Give the ALL PACKAGE APPLICATONS user (a) Read & Execute, (b) List Folder Contents, (c) Read 3] Restart the SQL Server Launchpad service on the SQL server. 4] Specify the address in the @thePYTHONscript variable using double backslashes between folder names in the path
*/
--This imports the file 'thePythonScriptFile.py' from the directory: C:\Temp DECLARE @thePYTHONscript NVARCHAR(MAX) = N' import sys sys.path += ["c:\\temp"] import thePythonScriptFile as theFile df_PYTHON_OUT, parmTau_OUT = theFile.compute_tau(df_SQL_IN, parmMultiplier_IN) ';
DECLARE @theSQLinputScript NVARCHAR(MAX) = N' SELECT cast(3.14159 as float) AS pi; ';
DECLARE @parmTau_OUT float;
EXEC sp_execute_external_script @language = N'Python' , @script = @thePYTHONscript , @input_data_1 = @theSQLinputScript , @input_data_1_name = N'df_SQL_IN' , @output_data_1_name = N'df_PYTHON_OUT' , @parallel = 1 , @params = N'@parmMultiplier_IN int, @parmTau_OUT float out' , @parmMultiplier_IN = 2 , @parmTau_OUT = @parmTau_OUT OUT WITH RESULT SETS ((tau float));
DECLARE @msg varchar(200) = 'tau = ' + ISNULL(CAST(@parmTau_OUT as varchar(30)), 'null');
PRINT @msg;
