--prj_1023_CreateTable_T0_Machine_v2.sql -- This took 11 minutes 6 seconds to run -- it reads directly from the XML file
Use DB6_AssetManagement go IF OBJECT_ID('dbo.T0_Machine') IS NOT NULL DROP TABLE dbo.T0_Machine go CREATE TABLE T0_Machine(
[T00_PK] [int] IDENTITY(1,1) NOT NULL, chassis NVARCHAR(25), IP NVARCHAR(15), Deviceserial NVARCHAR(15), mac1 NVARCHAR(17), mac2 NVARCHAR(17), mac3 NVARCHAR(17), [USER] NVARCHAR(50), name NVARCHAR(50), manufacturer NVARCHAR(50), model NVARCHAR(50), os NVARCHAR(100), osserialnumber NVARCHAR(50), osproductkey NVARCHAR(50), servicepack smallINT, ram NVARCHAR(20), scantime NVARCHAR(50), Date_Uploaded as Date
);
--Finally, the following statement will load the XML file, parse the XML elements to columns, and insert into the Products table:
INSERT INTO T0_Machine ( chassis, IP, Deviceserial, mac1, mac2, mac3, [USER], name, manufacturer, model, os, osserialnumber, osproductkey, servicepack, ram , scantime, Date_Uploaded ) SELECT --X.machine.query('ip').value('.', 'NVARCHAR(15)'), --X.machine.query('user').value('.', 'NVARCHAR(50)')
X.machine.query('chassis').value('.', 'NVARCHAR(25)'), X.machine.query('ip').value('.', 'NVARCHAR(15)'), X.machine.query('deviceserial').value('.', 'NVARCHAR(15)'), X.machine.query('mac1').value('.', 'NVARCHAR(17)'), X.machine.query('mac2').value('.', 'NVARCHAR(17)'), X.machine.query('mac3').value('.', 'NVARCHAR(17)'), X.machine.query('user').value('.', 'NVARCHAR(50)'), X.machine.query('name').value('.', 'NVARCHAR(50)'), X.machine.query('manufacturer').value('.', 'NVARCHAR(50)'), X.machine.query('model').value('.', 'NVARCHAR(50)'), X.machine.query('os').value('.', 'NVARCHAR(100)'), X.machine.query('osserialnumber').value('.', 'NVARCHAR(50)'), X.machine.query('osproductkey').value('.', 'NVARCHAR(50)'), X.machine.query('servicepack').value('.', 'smallINT'), X.machine.query('ram').value('.', 'NVARCHAR(20)'), X.machine.query('scantime').value('.', 'NVARCHAR(50)'), GETDATE() as [Date_Uploaded]
FROM ( SELECT CAST(x AS XML) FROM OPENROWSET( BULK '\\SQL-SERVER\QI_Data_Storage\AssetManagement_2013_04_18.xml', --<== MOVE data to folder: N:\DATA\QI_Data_Storage SINGLE_BLOB) AS T(x) ) AS T(x) CROSS APPLY x.nodes('assets/client/machine') AS X(machine);
--Here are the results: select * --SELECT IP, [USER], LEN(ltrim(rtrim([user]))) as LenUser FROM T0_Machine --Order by LenUser desc
