        [HttpGet]
        public IActionResult Get()
        {
            try
            {
                //first:
                //return Ok(_repository.GetAllOrders());
                //Second - use the Mapper,
                //      and since the input (_repository.GetAllOrders()) is a collection, the two types must be a collection (i.e. IEnumerable<>)
                //Note Once you map the object_1 to object_2, Automapper knows how to map Collections of those objects to each other
                return Ok(_mapper.Map<IEnumerable<Order>, IEnumerable<OrderViewModel>>(_repository.GetAllOrders()));



            }
            catch (Exception ex)
            {
                _logger.LogError($"Failed to get Orders: {ex}");
                return BadRequest("Failed to get orders");
            }

        }

