using AutoMapper;
using DutchTreat.Data.Entities;
using DutchTreat.ViewModels;

//required to derive from 'Profile'

namespace DutchTreat.Data
{
    public class DutchMappingProfile : Profile
    {
        public DutchMappingProfile() //Constructor
        {
            //Create a map between 'Order' and the 'OrderViewModel' - Note both Namespaces are required above

            /*Simple version:----------------------------------------------------------------------------
            CreateMap<Order, OrderViewModel>();

            /* From Postman send: http://localhost:8899/api/orders/2
                Postman returns the viewmodel:
                            {
                                "orderId": 0,               //<=== This is not populating, it should be '2'
                                "orderDate": "2017-12-25T00:00:00",
                                "orderNumber": null
                            }
            ----------------------------------------------------------------------------------------------*/

            CreateMap<Order, OrderViewModel>() //convention based: will match properties by names/types
                .ForMember(o => o.OrderId, ex => ex.MapFrom(o => o.Id))
                //So when looking for 'OrderId', map it from the source 'Id' field
                .ReverseMap();  //This says use the information before it, and create a map in the reverse order (see OrdersController):
                                //var newOrder = _mapper.Map<OrderViewModel, Order>(model);
                                // and
                                //return Created($"/api/orders/{newOrder.Id}", _mapper.Map<Order,OrderViewModel>(newOrder));

            /*From Postman send: http://localhost:8899/api/orders/2
              Now it returns:
                             {
                                "orderId": 2,
                                "orderDate": "2017-12-25T00:00:00",
                                "orderNumber": null
                            }       */

        }
    }
}

--------------------------/Models/Mapping/Header_R_Map.cs

using System.ComponentModel.DataAnnotations.Schema;
using System.Data.Entity.ModelConfiguration;

namespace Test_01.Models
	{
	public class Header_R_Map : EntityTypeConfiguration<Header_R>
		{
		public Header_R_Map()
			{
			// Primary Key
			this.HasKey(t => t.Header_PK);

			// Properties
			this.Property(t => t.TheName)
				.HasMaxLength(255);

			this.Property(t => t.PhoneNumber)
				.HasMaxLength(11);

			this.Property(t => t.Header_RowVersion)
				.IsFixedLength()
				.HasMaxLength(8)
				.IsRowVersion();

			this.Property(t => t.Percent_Done_0_00_to_1_00).HasPrecision(3, 2);	//This had to be customized after the EF Power Tools
			}
		}
	}

