using System.ComponentModel.DataAnnotations.Schema;
using System.Data.Entity.ModelConfiguration;

namespace Test_01.Models
	{
	public class Detail_R_Map : EntityTypeConfiguration<Detail_R>
		{
		public Detail_R_Map()
			{
			// Primary Key
			this.HasKey(t => t.Detail_PK);

			// Properties
			this.Property(t => t.Detail_Category_nvarchar)
				.HasMaxLength(255);

			this.Property(t => t.Detail_R_RowVersion)
				.IsFixedLength()
				.HasMaxLength(8)
				.IsRowVersion();


			// Relationships
			this.HasOptional(t => t.Header_R)
				.WithMany(t => t.Detail_R)
				.HasForeignKey(d => d.Header_FK);

			}
		}
	}

