--LIST_reportName_dataSourceName_dataProvider_connectionString --https://social.msdn.microsoft.com/Forums/sqlserver/en-US/601a784f-e28f-49b2-a02d-aa0db24994ec/way-to-discover-all-fields-used-in-all-ssrs-reports?forum=sqlreportingservices
use ProductionReportServer go
WITH XMLNAMESPACES ( DEFAULT 'http://schemas.microsoft.com/sqlserver/reporting/2010/01/reportdefinition', 'http://schemas.microsoft.com/SQLServer/reporting/reportdesigner' AS rd ) SELECT ReportName = name ,DataSourceName = x.value('(@Name)[1]', 'VARCHAR(250)') ,DataProvider = x.value('(ConnectionProperties/DataProvider)[1]','VARCHAR(250)') ,ConnectionString = x.value('(ConnectionProperties/ConnectString)[1]','VARCHAR(250)') FROM ( SELECT C.Name,CONVERT(XML,CONVERT(VARBINARY(MAX),C.Content)) AS reportXML FROM ProductionReportServer.dbo.Catalog C WHERE C.Content is not null AND C.Type = 2
) a CROSS APPLY reportXML.nodes('/Report/DataSources/DataSource') r ( x ) ORDER BY name ;
