#'USAGE
$testcomputers = "LT-1039"
#'$exportLocation = 'C:\OfficeVersions.csv'

#'Test connection to each computer before getting the inventory info
foreach ($computer in $testcomputers) {
	if (Test-Connection -ComputerName $computer -Quiet -Count 2){
        	write-host "responded to PING:"$computer -ForegroundColor Green
			Add-Content -value $computer -path C:\ONE_Computer_RespondToPing.txt
		}else{
			write-host "No Response to PING:"$computer -ForegroundColor Red
			Add-Content -value $computer -path C:\ONE_Computer_DidNotPing.txt
		}
	}

 get-officeversion -infile C:\ONE_Computer_RespondToPing.txt -outfile C:\ONE_Computer_officeversions.csv


Get-Content C:\ONE_Computer_officeversions.csv


=============================================================================================
<#
.Synopsis
   Get-OfficeVersion 
.DESCRIPTION
   Gets the Office version from a list of computernames and returns a CSV of the Computername and Office Version (if available)
.EXAMPLE
 get-officeversion c:\test\servers.txt c:\test\officeversions.csv
.EXAMPLE
 get-officeversion -infile c:\test\servers.txt -outfile c:\test\officeversions.csv
.NOTES
   General notes
.PARAMETER infile
A path and a filename to a text file contains a list of the computernames or ip addresses to check the office version.
.PARAMETER Outfile
A path and filename of the output .csv file

#>
function Get-OfficeVersion
{
param(
    [Parameter(Mandatory=$true,ValueFromPipeline=$true,ValueFromPipelineByPropertyName=$true)]
    [string] $Infile,

    [Parameter(Mandatory=$true,ValueFromPipeline=$true,ValueFromPipelineByPropertyName=$true)]
    [string] $outfile
    )
#$outfile = 'C:\temp\office.csv'
#$infile = 'c:\temp\servers.txt'
Begin
    {
    }
 Process
    {
    $office = @()
    $computers = Get-Content $infile
    $i=0
    $count = $computers.count
    foreach($computer in $computers)
     {
     $i++
     Write-Progress -Activity "Querying Computers" -Status "Computer: $i of $count " `
      -PercentComplete ($i/$count*100)
        $info = @{}
        $version = 0
        try{
          $reg = [Microsoft.Win32.RegistryKey]::OpenRemoteBaseKey('LocalMachine', $computer) 
          $reg.OpenSubKey('software\Microsoft\Office').GetSubKeyNames() |% {
            if ($_ -match '(\d+)\.') {
              if ([int]$matches[1] -gt $version) {
                $version = $matches[1]
              }
            }    
          }
          if ($version) {
            Write-Debug("$computer : found $version")
            switch($version) {
                "7" {$officename = 'Office 97' }
                "8" {$officename = 'Office 98' }
                "9" {$officename = 'Office 2000' }
                "10" {$officename = 'Office XP' }
                "11" {$officename = 'Office 97' }
                "12" {$officename = 'Office 2003' }
                "13" {$officename = 'Office 2007' }
                "14" {$officename = 'Office 2010' }
                "15" {$officename = 'Office 2013' }
                "16" {$officename = 'Office 2016' }
                default {$officename = 'Unknown Version'}
            }

          }
          }
          catch{
              $officename = 'Not Installed/Not Available/Not Answering'
          }
    $info.Computer = $computer
    $info.Name= $officename
    $info.version =  $version

    $object = new-object -TypeName PSObject -Property $info
    $office += $object
    }
    $office | select computer,version,name | Export-Csv -NoTypeInformation -Path $outfile
    }
}
  write-output ("Done")

monitor_a_server_SHUTDOWN_and_RESTART http://techibee.com/powershell/power-ping-a-script-to-monitor-your-server-restart/706 ===================================USAGE #'save this into restart-monitor.ps1 and excute it from powershell window >.Restart-Monitor.ps1 -computer server1 -timeout 10 ====================================
Param ( [Parameter(ValueFromPipeline=$False,Mandatory=$True)] [string]$computer, [Parameter(ValueFromPipeline=$False,Mandatory=$False)] [int]$timeout=5 )
$MAX_PINGTIME = $timeout * 60 $max_iterations = $MAX_PINGTIME/5 $Notification_timeout = 10 # in seconds
function Show-notification {
param($type,$text,$title)
#load Windows Forms and drawing assemblies [reflection.assembly]::loadwithpartialname("System.Windows.Forms") | Out-Null [reflection.assembly]::loadwithpartialname("System.Drawing") | Out-Null #define an icon image pulled from PowerShell.exe $icon=[system.drawing.icon]::ExtractAssociatedIcon((join-path $pshome powershell.exe)) $notify = new-object system.windows.forms.notifyicon $notify.icon = $icon $notify.visible = $True #define the tool tip icon based on the message type switch ($messagetype) { "Error" { $messageIcon=[system.windows.forms.tooltipicon]::Error} "Info" {$messageIcon=[system.windows.forms.tooltipicon]::Info} "Warning" {$messageIcon=[system.windows.forms.tooltipicon]::Warning} Default {$messageIcon=[system.windows.forms.tooltipicon]::None} }
#display the balloon tipe $notify.showballoontip($Notification_timeout,$title,$text,$type) }
function ping-host { param($pc) $status = Get-WmiObject -Class Win32_PingStatus -Filter "Address='$pc'" if( $status.statuscode -eq 0) { return 1 } else { return 0 } }
if(ping-host -pc $computer) { Write-Host "$computer is online; Waiting for it to go offline" $status = "online" for ($i=0; $i -le $max_iterations; $i++) { if (!(ping-host -pc $computer )) { break } Start-Sleep -Seconds 5 if($i -eq $max_iterations) { Write-Host "$computer never went down in last $timeout minutes" Write-Host "Check that reboot is initiated properly" show-notification -type "error" -text "$computer is still ONLINE; Check that reboot is initiated properly" -title "Computer is not rebooting" exit } }
Write-Host "$computer is offline now; monitoring for online status"
} else { Write-Host "$computer is offline; Monitoring for online status" $status = "offline" }
for ($i=0; $i -le $max_iterations; $i++) { if ((ping-host -pc $computer )) { break }
Start-Sleep -Seconds 5 if($i -eq $max_iterations) { Write-Host "Your computer never came back online in last $MAX_PINGTIME seconds" Write-Host "Check that nothing is preventing starup" show-notification -type "error" -text "$Computer is NOT coming online; Something is preventing its startup" -title "Computer failed to start" exit } }
Write-Host "Your computer is Online Now; Task done; exiting" show-notification -type "info" -text "$Computer is online" -title "$Computer successfully restarted"

    #************************************************************************************************************************************
# FileName:     Delete-DataSources.ps1
# Date:         2015/04/23
# Author:       Hugh Scott
#
# Description:
# This script finds data sources with no dependencies in SSRS and removes them.
#
# Parameters:
#   $serverBase     - base URL for the server to check (ie, myserver.mydomain.com)
#   [$WhatIf]       - Option wwitch parameter to prevent actual deleting of objects (will list out reports that need to be deleted)
#***********************************************************************************************************************************
[CmdletBinding()]
Param(
    [Parameter(Mandatory=$true,Position=0)]
    [string]$serverBase,
    [Parameter(Mandatory=$false,Position=1)]
    [switch]$WhatIf
)

$url = "http://$serverBase/reportserver/ReportService2010.asmx?WSDL"
$ssrs = New-WebServiceProxy -uri $url -UseDefaultCredential -Namespace "ReportingWebService"

$outFile = ".\DeleteItems_$serverBase.txt"

# Connection to Web Service, grab all data sources
$items = $ssrs.ListChildren("/", $true) | where-object {$_.typename -eq "DataSource"}
foreach($item in $items) {

    $dependencies = $ssrs.ListDependentItems($item.Path)
    $dependentReports = $dependencies.Count

    if($dependencies.Count -eq 0){
        [string]$itemName = $item.Path
        if($WhatIf){

            Write-Host "Item $itemName would be deleted."
            Add-Content $outFile "Item $itemName would be deleted."
        } else {
            try {
                $ssrs.DeleteItem($item.Path)
                Write-Host "Item $itemName deleted."
                Add-Content $outFile "Deleted item $itemName ."
            } catch [System.Exception] {
                $Msg = $_.Exception.Message
                Write-Host $itemName $Msg
                Add-Content $itemName $msg
            }
        }
    }
}
