Function Write-toFile{ <# .SYNOPSIS Writing information to file .DESCRIPTION Function to write information to file #> Param ([string]$WriteLine) Out-File $XMLFile -encoding utf8 -input $WriteLine -append Write-Host $WriteLine }
Function Write-ToLog([string]$message, [string]$file) { <# .SYNOPSIS Writing log to the logfile .DESCRIPTION Function to write logging to a logfile. This should be done in the End phase of the script. #> If(-not($file)){$file=$LogFile} $Date = $(get-date -uformat %Y-%m-%d-%H.%M.%S) $message = "$Date | `t$message" Write-Verbose $message Write-Host $message #Write Log to log file Without ASCII not able to read with tracer. Out-File $file -encoding ASCII -input $message -append }
Function GetSuperSededList {
$Script:countAllUpdates = 0 $Script:countSupersededAll = 0 $Script:countSupersededLastLevel = 0 $Script:countSupersededExclusionPeriod = 0 $Script:countSupersededLastLevelExclusionPeriod = 0 $Script:countDeclined = 0
$Prop = [ordered]@{} $ErrorActionPreference = "Stop" foreach($update in $allUpdates) {
$Script:countAllUpdates++
if ($update.IsDeclined) { $Script:countDeclined++ }
if (!$update.IsDeclined -and $update.IsSuperseded) { $Script:countSupersededAll++
if (!$update.HasSupersededUpdates) { $Script:countSupersededLastLevel++ } ################### if ($update.CreationDate -lt (get-date).AddDays(-$ExclusionPeriod)) { #"$($update.Id.UpdateId.Guid), $($update.Id.RevisionNumber), $($update.Title), $($update.KnowledgeBaseArticles), $($update.SecurityBulletins), $($update.HasSupersededUpdates)" | Out-File $outSupersededExList -Append "$($update.Title), $($update.KnowledgeBaseArticles), $($update.ArrivalDate), $($update.SecurityBulletins), $($update.UpdateClassificationTitle), $($update.ProductTitles), $($update.HasSupersededUpdates)" | Out-File $outSupersededExList -Append $Script:countSupersededExclusionPeriod++ if (!$update.HasSupersededUpdates) { $Script:countSupersededLastLevelExclusionPeriod++ } }
"$($update.Id.UpdateId.Guid), $($update.Id.RevisionNumber), $($update.Title), $($update.KnowledgeBaseArticles), $($update.ArrivalDate), $($update.SecurityBulletins), $($update.HasSupersededUpdates)" | Out-File $outSupersededList -Append
$Prop.Title = [string]$update.Title $Prop."KB Article" = [string]$update.KnowledgeBaseArticles $Prop."Arrival Date" = [string]$update.ArrivalDate $Prop.Classification = [string]$update.UpdateClassificationTitle $Prop."Product Title" = [string]$update.ProductTitles $Prop."Has Superseded Updates" = [string]$update.HasSupersededUpdates
New-Object PSObject -property $Prop } }
}
