foreach ($update in $allUpdates) {
if (!$update.IsDeclined -and $update.IsSuperseded) { if ($update.CreationDate -lt (get-date).AddDays(-$ExclusionPeriod)) {
$i++ $percentComplete = "{0:N2}" -f (($updatesDeclined/$countSupersededAll) * 100) Write-Progress -Activity "Declining Updates" -Status "Declining update #$i/$countSupersededAll - $($update.Id.UpdateId.Guid)" -PercentComplete $percentComplete -CurrentOperation "$($percentComplete)% complete" try { $update.Decline() $updatesDeclined++ } catch [System.Exception] { Write-Host "$WsusServer failed to decline update $($update.Id.UpdateId.Guid). Error:" $_.Exception.Message Write-EventLog -LogName $Eventlog -EventID 21031 -Message "$WsusServer failed to decline update $($update.Id.UpdateId.Guid). Error: $error[0]" -Source $EventSource -EntryType Error } } } }
}
Write-ToLog " Declined $updatesDeclined updates." #if ($updatesDeclined -ne 0) { # Copy-Item -Path $outSupersededList -Destination $outSupersededListBackup -Force # Write-ToLog " Backed up list of superseded updates to $outSupersededListBackup" #}
} else { Write-ToLog "TrialRun flag is set to $TrialRun. Skipped declining updates"
if(Test-Path $outSupersededExList){ Import-csv $outSupersededExList | ConvertTo-HTML -head $table | Out-File $outSupersededExHTM}
}
}
Function Decline-Itanium{
