if ($TrialRun) {Write-ToLog "NOTE: TrialRun flag is set to $TrialRun. Recording all Preview updates..."}Else{Write-ToLog "NOTE: TrialRun flag is set to $TrialRun. Continuing with declining updates..."}
Write-ToLog "Searching for Preview updates..." #$Prev = $WsusServerAdminProxy.GetUpdates() | ?{-not $_.IsDeclined -and $_.Title -match "Preview"} $Prev = $GrabUpdates | where-object {$_.Title -match "Preview"} $Script:Prevcount = $Prev.count
