Function UpdateListMaint{
Write-ToLog "CleanUpdatelist is set to $CleanUpdatelist. Cleaning the UpdateList folder." Write-ToLog "Deleting $CleanULNumber days old files and containers." Try{ get-childitem -Path $ulpath -recurse | where-object {$_.lastwritetime -lt (get-date).addDays(-$CleanULNumber)} | Foreach-Object { remove-item $_.FullName -force -recurse} get-ChildItem $ulpath -recurse | Where-Object {$_.PSIsContainer -eq $True} | Where-Object {$_.GetFiles().Count -eq 0} | Foreach-Object { remove-item $_.FullName -recurse} Write-ToLog "Successfully removed files that are older than $CleanULNumber." } Catch { Write-ToLog "Failed to clean the UpdateList files/folder. Error: $Error[0]" Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Failed to clean the UpdateList files/folder. Error: $Error[0]" -Source $EventSource -EntryType Error Write-ToLog "Please check the files manually." If($EmailReport) { $Body = ConvertTo-Html -head $CStyle -Body "Failed to clean the UpdateList files/folder. Error: $Error[0]. Please check the files manually." | Out-String $Body = $Body.Replace("<table>`r`n</table>", "") SendEmailStatus -From $From -To $To -Subject $Subject -SmtpServer $SmtpServer -BodyAsHtml $True -Body $Body Write-ToLog "Sending Mail..." }
}
}
############## Main Script ###############
Write-ToLog "####### Starting the $scriptName Script #######" Write-ToLog "" Write-ToLog "..." if ($TrialRun -and $DeclineLastLevelOnly) { Write-ToLog "Using TrialRun and DeclineLastLevelOnly switches together is not allowed." Write-ToLog "" Write-ToLog "Exiting..." Exit }
If (!(Test-Path -path $ulpath)){ New-Item -ItemType Directory $ulpath -force | Out-null }
$jeffobjects = Foreach ($WsusServer in $servers) {
$error.clear() $StartScript = Get-Date
$outSupersededList = "$ulpath\" + "AllSupersededUpdates-$WsusServer.csv" $outSupersededListBackup = "$ulpath\" + "SupersededUpdatesBackup-$WsusServer.csv" $outSupersededExList = "$ulpath\" + "SupersededUpdates-Over-$ExclusionPeriod-$WsusServer.csv" $outSupersededExHTM = "$ulpath\" + "SupersededUpdates-Over-$ExclusionPeriod-$WsusServer.htm" $outSupersededHTM = "$ulpath\" + "AllSupersededUpdates-$WsusServer.htm" $IThtmfile = "$ulpath\" + "Itanium-Updates-$WsusServer.htm" $XPhtmfile = "$ulpath\" + "XP-Updates-$WsusServer.htm" $Prevhtmfile = "$ulpath\" + "Preview-$WsusServer.htm" $Betahtmfile = "$ulpath\" + "Beta-$WsusServer.htm" $Win10Nexthtmfile = "$ulpath\" + "Win10Next-$WsusServer.htm" $ServerNexthtmfile = "$ulpath\" + "ServerNext-$WsusServer.htm" $IE7htmfile = "$ulpath\" + "IE7-$WsusServer.htm" $IE8htmfile = "$ulpath\" + "IE8-$WsusServer.htm" $IE9htmfile = "$ulpath\" + "IE9-$WsusServer.htm" $IE10htmfile = "$ulpath\" + "IE10-$WsusServer.htm" $Embhtmfile = "$ulpath\" + "Embedded-$WsusServer.htm" $Arm64htmfile = "$ulpath\" + "Arm64-$WsusServer.htm"
Function SendMail { If($EmailReport) { $Body = ConvertTo-Html -head $CStyle -Body "Error on $WsusServer. Error: $Error[0]" | Out-String SendEmailStatus -From $From -To $To -Subject $Subject -SmtpServer $SmtpServer -BodyAsHtml $True -Body $Body Write-ToLog "Sending Mail..." } }
$Props = [ordered]@{}
try {
if ($UseSSL) { Write-ToLog "Connecting to WSUS server $WsusServer on Port $PortNumber using SSL... " } Else { Write-ToLog "Connecting to WSUS server $WsusServer on Port $PortNumber... " }
[reflection.assembly]::LoadWithPartialName("Microsoft.UpdateServices.Administration") | out-null $WsusServerAdminProxy = [Microsoft.UpdateServices.Administration.AdminProxy]::GetUpdateServer($WsusServer,$UseSSL,$PortNumber);
} catch [System.Exception] { $err1 = $_.Exception.Message Write-ToLog "Failed to connect $WsusServer. Error: $err1" Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Error running the $scriptName Script on $WsusServer. Error: $err1" -Source $EventSource -EntryType Error Write-ToLog "Please check the logs." If($EmailReport) { $Body = ConvertTo-Html -head $CStyle -Body "Failed to connect $WsusServer. Error: $err1. Script exited." | Out-String $Body = $Body.Replace("<table>`r`n</table>", "") SendEmailStatus -From $From -To $To -Subject $Subject -SmtpServer $SmtpServer -BodyAsHtml $True -Body $Body Write-ToLog "Sending Mail..." } Write-ToLog "Exiting..." Exit }
if ($WsusServerAdminProxy) {Write-ToLog "Connected to $WsusServer."} if ($TrialRun) {Write-ToLog "NOTE: TrialRun flag is set to $TrialRun. Recording all superseded updates..."}Else{Write-ToLog "NOTE: TrialRun flag is set to $TrialRun. Continuing with declining updates..."}
$Props."Servername" = ("$wsusserver")
Write-ToLog "Collecting a list of updates from $WsusServer... Please wait..."
Try{$allUpdates = $WsusServerAdminProxy.GetUpdates()} Catch [System.Exception] { $err1 = $_.Exception.Message Write-ToLog "Failed to collect a list of updates from $WsusServer. Error: $Error[0]" Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Error running the $scriptName Script on $WsusServer. Error: $Error[0]" -Source $EventSource -EntryType Error Write-ToLog "Please check the logs." If($EmailReport) { $Body = ConvertTo-Html -head $CStyle -Body "Failed to collect a list of updates from $WsusServer. Error: $Error[0]. Please check the server manually. Script exited." | Out-String $Body = $Body.Replace("<table>`r`n</table>", "") SendEmailStatus -From $From -To $To -Subject $Subject -SmtpServer $SmtpServer -BodyAsHtml $True -Body $Body Write-ToLog "Sending Mail..." } Write-ToLog "Exiting..." Exit }
Write-ToLog "Done"
Write-ToLog "Parsing the list of updates... " -NoNewLine
Try{Decline-Superseded{};$Props."All Superseded" = ("$script:countSupersededAll");$Props."Superseded > $ExclusionPeriod" = ("$script:countSupersededExclusionPeriod")} Catch{Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Unable to run Decline-SupersededUpdatesWithExclusionPeriod function on $WsusServer" -Source $EventSource -EntryType Error; SendMail{}}
Write-ToLog "Decline-SupersededUpdates function..... Done." Write-ToLog ""
Write-ToLog "Grabbing more updates info from $WsusServer..." #$GrabUpdates= $WsusServerAdminProxy.GetUpdates() | ?{-not $_.IsDeclined} $GrabUpdates = $allUpdates | where-object {-not $_.IsDeclined}
If (!$SkipItanium) { Try {Decline-Itanium{};$Props."Itanium" = ("$Script:Itancount")} Catch{Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Unable to run Decline-WsusItaniumUpdates function on $WsusServer" -Source $EventSource -EntryType Error; SendMail{}} } Else { Write-ToLog "SkipItanium is set to $SkipItanium. Skipping Decline-WSusItaniumUpdates Function." $its = "Skipped" $Props."Itanium" = ([string]$its) }
If (!$SkipXP) { Try{Decline-XPUpdates{};$Props."Windows XP" = ($Script:XPcount)} Catch{Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Unable to run Decline-XPUpdates function on $WsusServer" -Source $EventSource -EntryType Error; SendMail{}} } Else { Write-ToLog "SkipXP is set to $SkipXP. Skipping Decline-XPUpdates Function." $xps = "Skipped" $Props."Windows XP" = ([string]$xps) }
If (!$SkipPrev) { Try{Decline-Preview{};$Props."Previews" = ("$script:Prevcount")} Catch{Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Unable to run Decline-Preview function on $WsusServer" -Source $EventSource -EntryType Error; SendMail{}} } Else { Write-ToLog "SkipPrev is set to $SkipPrev. Skipping Decline-Preview Function." $pvs = "Skipped" $Props."Previews" = ([string]$pvs) }
If (!$SkipBeta) { Try{Decline-Beta{};$Props."Beta" = ("$Script:Betacount")} Catch{Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Unable to run Decline-Beta function on $WsusServer" -Source $EventSource -EntryType Error; SendMail{}} } Else { Write-ToLog "SkipBeta is set to $SkipBeta. Skipping Decline-Beta Function." $bts = "Skipped" $Props."Beta" = ([string]$bts) }
If (!$SkipWwin10Next) { Try{Decline-Win10Next{};$Props."Windows 10 Next" = ("$Script:Win10Nextcount")} Catch{Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Unable to run Decline-Win10Next function on $WsusServer" -Source $EventSource -EntryType Error; SendMail{}} } Else { Write-ToLog "SkipWin10Next is set to $SkipWin10Next. Skipping Decline-Win10Next Function." $wns = "Skipped" $Props."Windows 10 Next" = ([string]$wns) }
If (!$SkipServerNext) { Try{Decline-ServerNext{};$Props."Server Next" = ($script:ServerNextcount)} Catch{Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Unable to run Decline-ServerNext function on $WsusServer" -Source $EventSource -EntryType Error; SendMail{}} } Else { Write-ToLog "SkipServerNext is set to $SkipServerNext. Skipping Decline-ServerNext Function." $sns = "Skipped" $Props."Server 10 Next" = ([string]$sns) }
If (!$SkipIE7) { Try{Decline-IE7{};$Props."IE 7" = ($Script:IE7count)} Catch{Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Unable to run Decline-IE7 function on $WsusServer" -Source $EventSource -EntryType Error; SendMail{}} } Else { Write-ToLog "SkipIE7 is set to $SkipIE7. Skipping Decline-IE7 Function." $i7s = "Skipped" $Props."IE 7" = ([string]$i7s) }
If (!$SkipIE8) { Try{Decline-IE8{};$Props."IE 8" = ($Script:IE8count)} Catch{Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Unable to run Decline-IE8 function on $WsusServer" -Source $EventSource -EntryType Error; SendMail{}} } Else { Write-ToLog "SkipIE8 is set to $SkipIE8. Skipping Decline-IE8 Function." $i8s = "Skipped" $Props."IE 8" = ([string]$i8s) }
If (!$SkipIE9) { Try{Decline-IE9{};$Props."IE 9" = ($Script:IE9count)} Catch{Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Unable to run Decline-IE9 function on $WsusServer" -Source $EventSource -EntryType Error; SendMail{}} } Else { Write-ToLog "SkipIE9 is set to $SkipIE9. Skipping Decline-IE9 Function." $i9s = "Skipped" $Props."IE 9" = ([string]$i9s)
}
If (!$SkipIE10) { Try{Decline-IE10{};$Props."IE 10" = ($Script:IE10count)} Catch{Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Unable to run Decline-IE10 function on $WsusServer" -Source $EventSource -EntryType Error; SendMail{}} } Else { Write-ToLog "SkipIE10 is set to $SkipIE10. Skipping Decline-IE10 Function." $i0s = "Skipped" $Props."IE 10" = ([string]$i0s)
}
If (!$SkipEmbedded) { Try{Decline-Embedded{};$Props."Windows Embedded" = ($Script:Embeddedcount)} Catch{Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Unable to run Decline-Embedded function on $WsusServer" -Source $EventSource -EntryType Error; SendMail{}} } Else { Write-ToLog "SkipEmbedded is set to $SkipEmbedded. Skipping Decline-Embedded Function." $ems = "Skipped" $Props."Windows Embedded" = ([string]$ems) }
If (!$SkipArm64) { Try{Decline-ARM64{};$Props."ARM64 Based" = ($Script:Arm64count)} Catch{Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Unable to run Decline-ARM64 function on $WsusServer" -Source $EventSource -EntryType Error; SendMail{}} } Else { Write-ToLog "SkipArm64 is set to $SkipArm64. Skipping Decline-ARM64 Function." $arm = "Skipped" $Props."ARM64 Based" = ([string]$arm) }
if ($error) { Write-EventLog -LogName $Eventlog -EventID 21021 -Message "Error running the $scriptName Script on $WsusServer. Error: $error{0}" -Source $EventSource -EntryType Error SendMail{} } Else {
Write-EventLog -LogName $Eventlog -EventID 21020 -Message "$scriptName Script has completed successfully on $WsusServer." -Source $EventSource -EntryType Information }
#$row = $table.NewRow()
Write-ToLog "" Write-ToLog "Done with $WsusServer... " Write-ToLog "" Write-ToLog "==========================" Write-ToLog "Overall Summary for $WsusServer" Write-ToLog "==========================" Write-ToLog " Total Superseded Updates (Older than $ExclusionPeriod days) = $script:countSupersededExclusionPeriod" Write-ToLog " Total Itanium Updates = $script:Itancount. $its" Write-ToLog " Total Windows XP Updates = $Script:XPcount. $xts" Write-ToLog " Total Preview Updates = $Script:Prevcount. $pvs" Write-ToLog " Total Beta Updates = $Script:Betacount. $bts" Write-ToLog " Total Windows 10 Next Updates = $Script:Win10Nextcount. $wns" Write-ToLog " Total Windows Server Next Updates = $Script:ServerNextcount. $sns" Write-ToLog " Total ARM64-Based Updates = $Script:Arm64count. $arm" Write-ToLog " Total Windows Embedded Updates = $Script:Embeddedcount. $ems" Write-ToLog " Total IE 7 Updates = $Script:IE7count. $i7s" Write-ToLog " Total IE 8 Updates = $Script:IE8count. $i8s" Write-ToLog " Total IE 9 Updates = $Script:IE9count. $i9s" Write-ToLog " Total IE 10 Updates = $Script:IE10count. $i0s"
Write-ToLog "" If (!$TrialRun){Write-ToLog "These Updates were declined, unless Skipped."}Else{Write-ToLog "Updates were ONLY recorded. See UpdatesList folder."} Write-ToLog "==========================" Write-ToLog "==========================" Write-ToLog ""
$Props."TrialRun" = ($TrialRun)
$StopScript = Get-Date $timespan = new-timespan -seconds $(($StopScript-$startScript).totalseconds) $ScriptTime = '{0:00}h:{1:00}m:{2:00}s' -f $timespan.Hours,$timespan.Minutes,$timespan.Seconds Write-ToLog "$WsusServer Run Time: $ScriptTime" Write-ToLog "==========================" Write-ToLog ""
$Props."Run Time" = ($ScriptTime)
New-Object PSObject -property $Props }
$jeffobjects | Select $Table | ConvertTo-HTML -head $CStyle | Out-File $Overallhtmfile
If($CleanUpdatelist){UpdateListMaint{}}
If($EmailReport) { $Body = "<p class=MsoTitle><span style='font-size:20pt;font-family:Verdana,sans-serif'>$ReportTitle<o:p></o:p></span></p>" $Body += $jeffobjects | Select $Table | ConvertTo-HTML -head $CStyle -PostContent "<h6>Created $(Get-Date)</h6><br>$from" SendEmailStatus -From $From -To $To -Subject $Subject -SmtpServer $SmtpServer -BodyAsHtml $True -Body $Body }
Write-ToLog "====> Done <====" Write-ToLog "All target WSUS/SUP servers have been completed." Write-ToLog "See $Overallhtmfile." Write-ToLog "=========================================="
=================================================================================================

#'https://gallery.technet.microsoft.com/scriptcenter/Synchronize-all-WSUS-00c899c2
#'Start-WsusServerSync.ps1

Param(
	$WsusServer = ([system.net.dns]::GetHostByName('localhost')).hostname,
	[switch]$Recursive,
	[bool]$TrialRun = $True,
	[int]$SleepTime = 60,
	[string]$SmtpServer = "smtp.company.com",
	[string]$From = "wsus@company.com",
	[string]$To = "wsus@company.com",
	[string]$Subject = "WSUS Server Synchronization.",
	[switch]$EmailLog
)
Begin
{	$script:CurrentErrorActionPreference = $ErrorActionPreference
	$script:Output = @()
	$script:ProcessedServers = @()
	$WsusAssembly = [reflection.assembly]::LoadWithPartialName("Microsoft.UpdateServices.Administration")
	If($WsusAssembly -eq $Null)
	{	throw "Loading Microsoft.UpdateServices.Administration failed. Are you running this on a machine with the WSUS 3.0 SP2 Administration Console installed? http://technet.microsoft.com/en-us/library/dd939875(v=ws.10).aspx"}

	$ErrorActionPreference = "SilentlyContinue"
	If($EmailLog)
	{	If($Recursive)
		{	$Table = @{Name="Parent Wsus Server";expression={$_.ParentWsusServer}},@{Name="Wsus Server";expression={$_.WsusServer}},@{Name="Port Number";expression={$_.PortNumber}},@{Name="Using SSL";expression={$_.UsingSSL}},@{Name="Version";expression={$_.Version}},@{Name="Start";expression={$_.Start}},@{Name="Finish";expression={$_.Finish}}
		}
		Else
		{	$Table = @{Name="Wsus Server";expression={$_.WsusServer}},@{Name="Port Number";expression={$_.PortNumber}},@{Name="Using SSL";expression={$_.UsingSSL}},@{Name="Version";expression={$_.Version}},@{Name="Start";expression={$_.Start}},@{Name="Finish";expression={$_.Finish}}
		}
		If($TrialRun -eq $False)
		{	$Table += @{Name="Categories";expression={$_.Categories}},@{Name="Updates";expression={$_.Updates}},@{Name="Approvals";expression={$_.Approvals}},@{Name="LastSyncResult";expression={$_.LastSyncResult}}
		}
		$Style = "<Style>BODY{font-size:12px;font-family:verdana,sans-serif;color:navy;font-weight:normal;}" + `
		"TABLE{border-width:1px;cellpadding=10;border-style:solid;border-color:navy;border-collapse:collapse;}" + `
		"TH{font-size:12px;border-width:1px;padding:10px;border-style:solid;border-color:navy;}" + `
		"TD{font-size:10px;border-width:1px;padding:10px;border-style:solid;border-color:navy;}</Style>"
		Function SendEmailStatus($From, $To, $Subject, $SmtpServer, $BodyAsHtml, $Body)
		{	$SmtpMessage = New-Object System.Net.Mail.MailMessage $From, $To, $Subject, $Body
			$SmtpMessage.IsBodyHTML = $BodyAsHtml
			$SmtpClient = New-Object System.Net.Mail.SmtpClient $SmtpServer
			$SmtpClient.Send($SmtpMessage)
			If($? -eq $False){Write-Warning "$($Error[0].Exception.Message) | $($Error[0].Exception.GetBaseException().Message)"}
			$SmtpMessage.Dispose()
			rv SmtpClient
			rv SmtpMessage
		}
	}

	function Get-HKLMValue
	{	Param(
  		[string]$computername=".",
  		[string]$key = "SOFTWARE\Microsoft\Update Services\Server\Setup",
  		[string]$value,
  		[switch]$REG_SZ,
  		[switch]$REG_DWORD
  	)
		$HKLM = 2147483650
		$reg = [wmiclass]"\\$computername\root\default:StdRegprov"
		If($REG_SZ)
		{	$Result = $reg.GetStringValue($HKLM,$key,$value)
			If($Result.ReturnValue -eq 0){$Result.sValue}
		}
		If($REG_DWORD)
		{	$Result = $reg.GetDwordValue($HKLM,$key,$value)
			If($Result.ReturnValue -eq 0){$Result.uValue}
		}
	}

	function Start-Pause
	{	Param(
			[int]$SleepTime = 10,
			[int]$ID = 1,
			[int]$ParentID,
			[string]$Activity = "Just taking a quick breather after all that activity..."
		)
		for($x = 1 ; $x -le $SleepTime; $x++)
		{	If(!$ParentID)
			{	Write-progress -Activity $Activity -Status "Seconds Remaining: $($SleepTime-$x)" -PercentComplete ($x/$SleepTime*100) -ID $ID}
			Else	
			{	Write-progress -Activity $Activity -Status "Seconds Remaining: $($SleepTime-$x)" -PercentComplete ($x/$SleepTime*100) -ID $ID -ParentId $ParentID}
			Sleep 1
		}
		Write-progress -Activity $Activity -Status "Done sleeping..." -Completed -ID $ID
	}

	Function Sync-WsusServer
	{	Param(
			$WsusServer,
			$ParentWsusServer
		)
		Write-Progress -Activity "Processing server: $WsusServer" -Status "Started at $((get-date).DateTime)" -ID 2 -ParentID 1
		Write-Progress -Activity "Retrieving PortNumber value from the registry via StdRegprov ..." -Status "Started at $((get-date).DateTime)" -ID 3 -ParentID 2
		$PortNumber = Get-HKLMValue -Computername $WsusServer -value PortNumber -REG_DWORD
		Write-Progress -Activity "Retrieving UsingSSL value from the registry via StdRegprov ..." -Status "Started at $((get-date).DateTime)" -ID 3 -ParentID 2
		$UsingSSL = If((Get-HKLMValue -Computername $WsusServer -value UsingSSL -REG_DWORD) -eq 1){$True}Else{$False}
		If($UsingSSL)
		{ Write-Progress -Activity "Retrieving ServerCertificateName value from the registry via StdRegprov ..." -Status "Started at $((get-date).DateTime)" -ID 3 -ParentID 2
			$ServerCertificateName = Get-HKLMValue -Computername $WsusServer -value ServerCertificateName -REG_SZ
			If($ServerCertificateName){$WsusServer = $ServerCertificateName}
		}
		If($script:ProcessedServers -Contains $WsusServer)
		{	Write-Warning "$WsusServer appears to have already been processed. You may have a circular loop in your hierarchy."}
		Else
		{	$script:ProcessedServers += $WsusServer
			$Object = New-Object psobject
			If($Recursive)
			{	If(!$ParentWsusServer){$ParentWsusServer = "--"}
				$Object | Add-Member NoteProperty ParentWsusServer $ParentWsusServer
			}
			$Object | Add-Member NoteProperty WsusServer $WsusServer -PassThru | Add-Member NoteProperty PortNumber $PortNumber -PassThru | 
				Add-Member NoteProperty UsingSSL $UsingSSL -PassThru | Add-Member NoteProperty Version "" -PassThru |
				Add-Member NoteProperty Start (get-date).DateTime -PassThru | Add-Member NoteProperty Finish ""
			If($TrialRun -eq $False)
			{	$Object | Add-Member NoteProperty Categories "--" -PassThru | 
					Add-Member NoteProperty Updates "--" -PassThru | 
					Add-Member NoteProperty Approvals "--" -PassThru | 
					Add-Member NoteProperty LastSyncResult ""
			}
			Write-Progress -Activity "Connecting to UpdateServices AdminProxy..." -Status "Started at $((get-date).DateTime)" -ID 3 -ParentID 2
			$WsusServerAdminProxy = [Microsoft.UpdateServices.Administration.AdminProxy]::GetUpdateServer($WsusServer,$UsingSSL,$PortNumber)
			If ($? -eq $False)
			{	$Object.Version = $Error[0]
				Write-Warning "Failed to connect to $WsusServer $($Error[0])"
				$Object.Finish = (get-date).DateTime
				$Object
				If($EmailLog){$script:Output += $Object}
			}
			Else
			{	$Object.Version = $WsusServerAdminProxy.Version
				If($TrialRun -eq $False)
				{	Write-Progress -Activity "Connecting to the Subscription..." -Status "Started at $((get-date).DateTime)" -ID 3 -ParentID 2
					$Subscription = $WsusServerAdminProxy.GetSubscription();
					Write-Progress -Activity "Calling StartSynchronization on $WsusServer" -Status "Started at $((get-date).DateTime)" -ID 3 -ParentID 2
					$Subscription.StartSynchronization()
					$SynchronizationProgress = $Subscription.GetSynchronizationProgress()
					While ($SynchronizationProgress.Phase.ToString() -eq "NotProcessing")
					{	Start-Sleep -Milliseconds 100
						$SynchronizationProgress = $Subscription.GetSynchronizationProgress()
					}
					While ($SynchronizationProgress.Phase.ToString() -ne "NotProcessing")
					{	If($SynchronizationProgress.Phase.ToString() -eq "Categories")
						{$Object.Categories = $SynchronizationProgress.TotalItems}
						If($SynchronizationProgress.Phase.ToString() -eq "Updates")
						{	$Object.Updates = $SynchronizationProgress.TotalItems}
						If($SynchronizationProgress.Phase.ToString() -eq "Approvals")
						{$Object.Approvals = $SynchronizationProgress.TotalItems}
						Write-Progress -Activity "Synchronization Phase: $($SynchronizationProgress.Phase.ToString())" -Status "$($SynchronizationProgress.ProcessedItems) of $($SynchronizationProgress.TotalItems) items done..." -PercentComplete ($SynchronizationProgress.ProcessedItems*100/($SynchronizationProgress.TotalItems)) -ID 4 -ParentId 3
						$SynchronizationProgress = $Subscription.GetSynchronizationProgress()
					}
					$Object.LastSyncResult = $Subscription.GetLastSynchronizationInfo().Result
					Write-Progress -Activity "Synchronization Phase: $($SynchronizationProgress.Phase.ToString())" -Status "Complete." -ID 4 -ParentId 3 -Completed
				}
				$Object.Finish = (get-date).DateTime
				$Object
				If($EmailLog){$script:Output += $Object}
				If($Recursive -And $TrialRun -eq $False){Start-Pause -Activity "Processed $($script:ProcessedServers.Count) server(s). The script is pausing for $SleepTime seconds starting at $((get-date).DateTime)." -SleepTime $SleepTime -ID 3 -ParentID 2}
				If($Recursive)
				{	Write-Progress -Activity "Retrieving Downstream Servers on $WsusServer..." -Status "Started at $((get-date).DateTime)" -ID 3 -ParentID 2
					$WsusDownstreamServers = $WsusServerAdminProxy.GetDownstreamServers()
					If($WsusDownstreamServers){$WsusDownstreamServers | %{Sync-WsusServer -WsusServer $_.FullDomainName -ParentWsusServer $WsusServer}}
				}
			}
		}
	}
	Write-Progress -Activity "WSUS Server Synchronization." -Status "Started at $((get-date).DateTime)" -ID 1
}
Process
{	If($WsusServer)
	{	ForEach($Server in $WsusServer){Sync-WsusServer $Server}
	}
	Else
	{	Sync-WsusServer $_
	}
}
End
{	If($EmailLog){SendEmailStatus -From $From -To $To -Subject $Subject -SmtpServer $SmtpServer -BodyAsHtml $True -Body ($Output | Select $Table | ConvertTo-HTML -head $Style)}
	$ErrorActionPreference = $script:CurrentErrorActionPreference
}

#'WindowsUpdate.psm1
#'https://github.com/adbertram/Random-PowerShell-Work/blob/master/Software%20Updates/WindowsUpdate.psm1

Set-StrictMode -Version Latest

function Get-WindowsUpdate {
	<#
		.SYNOPSIS
			This function retrieves a list of Microsoft updates based on a number of different criteria for a remote
			computer. It will retrieve these updates over a PowerShell remoting session. It uses the update source set
			at the time of query. If it's set to WSUS, it will only return updates that are advertised to the computer
			by WSUS.

		.EXAMPLE
			PS> Get-WindowsUpdate -ComputerName FOO
		.PARAMETER ComputerName
			 A mandatory string parameter representing the FQDN of a computer. This is only mandatory is Session is
			 not used.
		.PARAMETER Credential
			 A optoional pscredential parameter representing an alternate credential to connect to the remote computer.
		.PARAMETER Session
			 A mandatory PSSession parameter representing a PowerShell remoting session created with New-PSSession. This
			 is only mandatory if ComputerName is not used.

		.PARAMETER Installed
			 A optional boolean parameter set to either $true or $false depending on if you'd like to filter the resulting
			 updates on this criteria.
		.PARAMETER Hidden
			 A optional boolean parameter set to either $true or $false depending on if you'd like to filter the resulting
			 updates on this criteria.
		.PARAMETER Assigned
			A optional boolean parameter set to either $true or $false depending on if you'd like to filter the resulting
			updates on this criteria.
		.PARAMETER RebootRequired
			A optional boolean parameter set to either $true or $false depending on if you'd like to filter the resulting
			updates on this criteria.
	#>
	[OutputType([System.Management.Automation.PSObject])]
	[CmdletBinding()]
	param
	(
		[Parameter(Mandatory, ParameterSetName = 'ByComputerName')]
		[ValidateNotNullOrEmpty()]
		[string]$ComputerName,

		[Parameter(ParameterSetName = 'ByComputerName')]
		[ValidateNotNullOrEmpty()]
		[pscredential]$Credential,

		[Parameter(Mandatory, ParameterSetName = 'BySession')]
		[ValidateNotNullOrEmpty()]
		[System.Management.Automation.Runspaces.PSSession]$Session,

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[ValidateSet('MicrosoftUpdate')]
		[string]$Source,

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[ValidateSet('True', 'False')]
		[string]$Installed = 'False',

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[ValidateSet('True', 'False')]
		[string]$Hidden,

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[ValidateSet('True', 'False')]
		[string]$Assigned,

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[ValidateSet('True', 'False')]
		[string]$RebootRequired
	)
	begin {
		$ErrorActionPreference = 'Stop'
		if (-not $Session) {
			$sessParams = @{
				ComputerName = $ComputerName
			}
			if ($PSBoundParameters.ContainsKey('Credential')) {
				$sessParams.Credential = $Credential
			}
			$Session = New-PSSession @sessParams
		}
	}
	process {
		try {
			$criteriaParams = @{}

			## Had to set these to string values because if they're boolean they will have a $false value even if
			## they aren't set.  I needed to check for a $null value.ided
			@('Installed', 'Hidden', 'Assigned', 'RebootRequired').where({ (Get-Variable -Name $_).Value }).foreach({
					$criteriaParams[$_] = if ((Get-Variable -Name $_).Value -eq 'True') {
						$true 
					} else {
						$false 
					}
				})
			$query = NewUpdateCriteriaQuery @criteriaParams
			Write-Verbose -Message "Using the update criteria query: [$($Query)]..."
			$searchParams = @{
				Session = $Session
				Query   = $query
			}
			if ($PSBoundParameters.ContainsKey('Source')) {
				$searchParams.Source = $Source
			}
			SearchWindowsUpdate @searchParams
		} catch {
			Write-Error $_.Exception.Message
		} finally {
			## Only clean up the session if it was generated from within this function. This is because updates
			## are stored in a variable to be used again by other functions, if necessary.
			if (($PSCmdlet.ParameterSetName -eq 'ByComputerName') -and (Test-Path Variable:\session)) {
				$session | Remove-PSSession
			}
		}
	}
}

function Install-WindowsUpdate {
	<#
		.SYNOPSIS
			This function retrieves all updates that are targeted at a remote computer, download and installs any that it
			finds. Depending on how the remote computer's update source is set, it will either read WSUS or Microsoft Update
			for a compliancy report.
			Once found, it will download each update, install them and then read output to detect if a reboot is required
			or not.

		.EXAMPLE
			PS> Install-WindowsUpdate -ComputerName FOO.domain.local
		.EXAMPLE
			PS> Install-WindowsUpdate -ComputerName FOO.domain.local,FOO2.domain.local			

		.EXAMPLE
			PS> Install-WindowsUpdate -ComputerName FOO.domain.local,FOO2.domain.local -ForceReboot
		.PARAMETER ComputerName
			 A mandatory string parameter representing one or more computer FQDNs.
		.PARAMETER Credential
			 A optional pscredential parameter representing an alternate credential to connect to the remote computer.

		.PARAMETER ForceReboot
			 An optional switch parameter to set if any updates on any computer targeted needs a reboot following update
			 install. By default, computers are NOT rebooted automatically. Use this switch to force a reboot.

		.PARAMETER AsJob
			 A optional switch parameter to set when activity needs to be sent to a background job. By default, this function 
			 waits for each computer to finish. However, if this parameter is used, it will start the process on each
			 computer and immediately return a background job object to then monitor yourself with Get-Job.
	#>
	[OutputType([void])]
	[CmdletBinding()]
	param
	(
		[Parameter(Mandatory)]
		[ValidateNotNullOrEmpty()]
		[string[]]$ComputerName,

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[pscredential]$Credential,

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[ValidateSet('MicrosoftUpdate')]
		[string]$Source,

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[switch]$ForceReboot,

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[switch]$AsJob
	)
	begin {
		$ErrorActionPreference = 'Stop'

		$scheduledTaskName = 'Windows Update Install'

	}
	process {
		try {
			$getParams = @{}
			if ($PSBoundParameters.ContainsKey('Credential')) {
				$getParams.Credential = $Credential
			}
			if ($PSBoundParameters.ContainsKey('Source')) {
				$getParams.Source = $Source
			}
			@($ComputerName).foreach({
					$getParams.ComputerName = $_
					if (-not (Get-WindowsUpdate @getParams)) {
						Write-Verbose -Message 'No updates needed to install. Skipping computer...'
					} else {
						$installProcess = {
							param($ComputerName, $TaskName, $Credential, $ForceReboot)
							$VerbosePreferences = 'Continue'
							try {
								$sessParams = @{ ComputerName = $ComputerName }
								if ($Credential) {
									$sessParams.Credential = $Credential
								}

								$session = New-PSSession @sessParams

								$scriptBlock = {
									$updateSession = New-Object -ComObject 'Microsoft.Update.Session'
									$objSearcher = $updateSession.CreateUpdateSearcher()
									if ($using:Source -eq 'MicrosoftUpdate') {
										$objSearcher.ServerSelection = 3
									}
									if ($updates = ($objSearcher.Search('IsInstalled=0'))) {
										$updates = $updates.Updates

										$downloader = $updateSession.CreateUpdateDownloader();
										$downloader.Updates = $updates;
										$downloadResult = $downloader.Download();
										if ($downloadResult.ResultCode -ne 2) {
											exit $downloadResult.ResultCode;
										}

										$installer = New-Object -ComObject Microsoft.Update.Installer;
										$installer.Updates = $updates;
										$installResult = $installer.Install();
										if ($installResult.RebootRequired) {
											exit 7;
										} else {
											$installResult.ResultCode
										}
									} else {
										exit 6;
									}
								}

								$taskParams = @{
									Session     = $session
									Name        = $TaskName
									Scriptblock = $scriptBlock
									PassThru    = $true
								}
								Write-Verbose -Message 'Creating scheduled task...'
								if (-not ($task = NewWindowsUpdateScheduledTask @taskParams)) {
									throw "Failed to create scheduled task."
								}

								Write-Verbose -Message "Starting scheduled task [$($task.TaskName)]..."

								$icmParams = @{
									Session      = $session
									ScriptBlock  = { Start-ScheduledTask -TaskName $args[0] }
									ArgumentList = $task.TaskName
									Verbose      = $true
								}
								Invoke-Command @icmParams

								$waitParams = @{
									ComputerName = $_
								}
								if ($Credential) {
									$waitParams.Credential = $Credential
								}
								Wait-ScheduledTask @waitParams -Name $task.TaskName

								$installResult = GetWindowsUpdateInstallResult -Session $session

								if ($installResult -eq 'NoUpdatesNeeded') {
									Write-Verbose -Message "No updates to install"
								} elseif ($installResult -eq 'RebootRequired') {
									if ($ForceReboot) {
										Restart-Computer -ComputerName $ComputerName -Force -Wait;
									} else {
										Write-Warning "Reboot required but -ForceReboot was not used."
									}
								} else {
									throw "Updates failed. Reason: [$($installResult)]"
								}

							} catch {
								$PSCmdlet.ThrowTerminatingError($_)
							} finally {
								Remove-ScheduledTask @getParams -Name $TaskName
							}
						}

						$blockArgs = $_, $scheduledTaskName, $Credential, $ForceReboot.IsPresent
						if ($AsJob.IsPresent) {
							Start-Job -ScriptBlock $installProcess -Name "$_ - Windows Update Install" -ArgumentList $blockArgs
						} else {
							Invoke-Command -ScriptBlock $installProcess -ArgumentList $blockArgs
						}
					}
				})
		} catch {
			Write-Error $_.Exception.Message
		} finally {
			# Remove any sessions created. This is done when processes aren't invoked under a PS job
			$sessParams = @{
				ComputerName = $ComputerName
			}
			if ($PSBoundParameters.ContainsKey('Credential')) {
				$sessParams.Credential = $Credential
			}
			@(Get-PSSession @sessParams).foreach({
					Remove-PSSession -Session $_
				})
		}
	}
}

function GetWindowsUpdateInstallResult {
	[OutputType([string])]
	[CmdletBinding()]
	param
	(
		[Parameter(Mandatory)]
		[System.Management.Automation.Runspaces.PSSession]$Session,

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[string]$ScheduledTaskName = 'Windows Update Install'
	)

	$sb = { (Get-ScheduledTask -TaskName $args[0] | Get-ScheduledTaskInfo).LastTaskResult }
	$resultCode = Invoke-Command -Session $Session -ScriptBlock $sb -ArgumentList $ScheduledTaskName
	switch -exact ($resultCode) {
		0   {
			'Installed'
			break
		}
		1   {
			'InProgress'
			break
		}
		2   {
			'Installed'
			break
		}
		3   {
			'InstalledWithErrors'
			break
		}
		4   {
			'Failed'
			break
		}
		5   {
			'Aborted'
			break
		}
		6   {
			'NoUpdatesNeeded'
			break
		}
		7   {
			'RebootRequired'
			break
		}
		267009 {
			'TimedOut'
			break
		}
		default {
			"Unknown exit code [$($_)]"
		}
	}
}

function NewUpdateCriteriaQuery {
	[OutputType([string])]
	[CmdletBinding()]
	param
	(
		[Parameter()]
		[bool]$Installed,

		[Parameter()]
		[bool]$Hidden,

		[Parameter()]
		[bool]$Assigned,

		[Parameter()]
		[bool]$RebootRequired
	)

	$conversion = @{
		Installed      = 'IsInstalled'
		Hidden         = 'IsHidden'
		Assigned       = 'IsAssigned'
		RebootRequired = 'RebootRequired'
	}

	$queryElements = @()
	$PSBoundParameters.GetEnumerator().where({ $_.Key -in $conversion.Keys }).foreach({
			$queryElements += '{0}={1}' -f $conversion[$_.Key], [int]$_.Value
		})
	$queryElements -join ' and '
}

function SearchWindowsUpdate {
	[OutputType()]
	[CmdletBinding()]
	param
	(
		[Parameter()]
		[string]$Query,

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[string]$Source,

		[Parameter()]
		[System.Management.Automation.Runspaces.PSSession]$Session
	)

	$scriptBlock = {
		$objSession = New-Object -ComObject 'Microsoft.Update.Session'
		$objSearcher = $objSession.CreateUpdateSearcher()
		if ($using:Source -eq 'MicrosoftUpdate') {
			$objServiceManager = New-Object -ComObject "Microsoft.Update.ServiceManager"
			$objSearcher.ServerSelection = 3
			$objServiceManager.Services | Where-Object { $_.Name -eq 'Microsoft Update' } | Foreach {
				$objSearcher.ServiceID = $_.ServiceID
			}
		}
		if ($updates = ($objSearcher.Search($args[0]))) {
			$updates = $updates.Updates
			## Save the updates needed to the file system for other functions to pick them up to download/install later.
			$updates | Export-CliXml -Path "$env:TEMP\Updates.xml"
			$updates
		}

	}
	Invoke-Command -Session $Session -ScriptBlock $scriptBlock -ArgumentList $Query
}

function NewWindowsUpdateScheduledTask {
	[OutputType([void])]
	[CmdletBinding()]
	param
	(
		[Parameter(Mandatory)]
		[System.Management.Automation.Runspaces.PSSession]$Session,

		[Parameter(Mandatory)]
		[string]$Name,

		[Parameter(Mandatory)]
		[ValidateNotNullOrEmpty()]
		[scriptblock]$Scriptblock,

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[pscredential]$Credential,

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[switch]$PassThru
	)

	if (TestWindowsUpdateScheduledTask -Session $Session -Name $Name) {
		Write-Verbose -Message "A windows update install task already exists. Removing..."
		Remove-ScheduledTask -ComputerName $Session.ComputerName -Name $Name
	}

	$createStartSb = {
		$action = New-ScheduledTaskAction -Execute 'powershell.exe' -Argument $args[1]
		$principal = New-ScheduledTaskPrincipal -UserId $args[3] -LogonType Password
		$settings = New-ScheduledTaskSettingsSet -StartWhenAvailable -Hidden
		$task = New-ScheduledTask -Action $action -Settings $settings -Principal $principal
		$regTask = Register-ScheduledTask -InputObject $task -TaskName $args[0]
		if ($args[2].IsPresent) {
			$regTask
		}
	}

	$psArgs = '-NonInteractive -NoProfile -Command "{0}"' -f $Scriptblock.ToString()

	$icmParams = @{
		Session      = $Session
		ScriptBlock  = $createStartSb
		ArgumentList = $Name, $psArgs, $PassThru
	}
	if ($PSBoundParameters.ContainsKey('Credential')) {
		$icmParams.ArgumentList += $Credential.UserName	
	} else {
		$icmParams.ArgumentList += 'SYSTEM'
	}

	Invoke-Command @icmParams

}

function TestWindowsUpdateScheduledTask {
	[OutputType([bool])]
	[CmdletBinding()]
	param
	(
		[Parameter(Mandatory)]
		[System.Management.Automation.Runspaces.PSSession]$Session,

		[Parameter(Mandatory)]
		[ValidateNotNullOrEmpty()]
		[string]$Name
	)
	$testScriptBlock = {
		if (Get-ScheduledTask -TaskName $args[0] -ErrorAction Ignore) {
			$true
		} else {
			$false
		}
	}

	Invoke-Command -Session $Session -ScriptBlock $testScriptBlock -ArgumentList $Name
}

function Wait-WindowsUpdate {
	<#
		.SYNOPSIS
			This function looks for any currently running background jobs that were created by Install-WindowsUpdate
			and continually waits for all of them to finish before returning control to the console.

		.EXAMPLE
			PS> Wait-WindowsUpdate

		.PARAMETER Timeout
			 An optional integer parameter representing the amount of seconds to wait for the job to finish.

	#>
	[OutputType([void])]
	[CmdletBinding()]
	param
	(
		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[int]$Timeout = 3600
	)
	process {
		try {
			if ($updateJobs = (Get-Job -Name '*Windows Update Install*').where({ $_.State -eq 'Running'})) {
				$timer = Start-Timer
				while ((Microsoft.PowerShell.Core\Get-Job -Id $updateJobs.Id | Where-Object { $_.State -eq 'Running' }) -and ($timer.Elapsed.TotalSeconds -lt $Timeout)) {
					Write-Verbose -Message "Waiting for all Windows Update install background jobs to complete..."
					Start-Sleep -Seconds 3
				}
				Stop-Timer -Timer $timer
			}
		} catch {
			Write-Error $_.Exception.Message
		}
	}
}

function Remove-ScheduledTask {
	<#
		.SYNOPSIS
			This function looks for a scheduled task on a remote system and, once found, removes it.

		.EXAMPLE
			PS> Remove-ScheduledTask -ComputerName FOO -Name Task1

		.PARAMETER ComputerName
			 A mandatory string parameter representing a FQDN of a remote computer.
		.PARAMETER Name
			 A mandatory string parameter representing the name of the scheduled task. Scheduled tasks can be retrieved
			 by using the Get-ScheduledTask cmdlet.
		.PARAMETER Credential
			 Specifies a user account that has permission to perform this action. The default is the current user.

			 Type a user name, such as 'User01' or 'Domain01\User01', or enter a variable that contains a PSCredential
			 object, such as one generated by the Get-Credential cmdlet. When you type a user name, you will be prompted for a password.

	#>
	[OutputType([void])]
	[CmdletBinding(SupportsShouldProcess)]
	param
	(
		[Parameter(Mandatory)]
		[ValidateNotNullOrEmpty()]
		[string]$ComputerName,

		[Parameter(Mandatory)]
		[ValidateNotNullOrEmpty()]
		[string]$Name,

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[pscredential]$Credential	
	)
	begin {
		$ErrorActionPreference = 'Stop'
	}
	process {
		try {
			$icmParams = @{
				ComputerName = $ComputerName
				ArgumentList = $Name
			}
			if ($PSBoundParameters.ContainsKey('Credential')) {
				$icmParams.Credential = $Credential
			}

			$sb = { 
				if ($task = Get-ScheduledTask -TaskName $args[0] -ErrorAction Ignore) {
					$task | Unregister-ScheduledTask -Confirm:$false
				}
			}

			if ($PSCmdlet.ShouldProcess("Remove scheduled task [$($Name)] from [$($ComputerName)]", '----------------------')) {
				Invoke-Command @icmParams -ScriptBlock $sb	
			}
		} catch {
			Write-Error -Message $_.Exception.Message
		}
	}
}

function Wait-ScheduledTask {
	<#
		.SYNOPSIS
			This function looks for a scheduled task on a remote system and, once found, checks to see if it's running.
			If so, it will wait until the task has completed and return control.

		.EXAMPLE
			PS> Wait-ScheduledTask -ComputerName FOO -Name Task1 -Timeout 120

		.PARAMETER ComputerName
			 A mandatory string parameter representing a FQDN of a remote computer.
		.PARAMETER Name
			 A mandatory string parameter representing the name of the scheduled task. Scheduled tasks can be retrieved
			 by using the Get-ScheduledTask cmdlet.
		.PARAMETER Timeout
			 A optional integer parameter representing how long to wait for the scheduled task to complete. By default,
			 it will wait 3600 seconds.
		.PARAMETER Credential
			 Specifies a user account that has permission to perform this action. The default is the current user.

			 Type a user name, such as 'User01' or 'Domain01\User01', or enter a variable that contains a PSCredential
			 object, such as one generated by the Get-Credential cmdlet. When you type a user name, you will be prompted for a password.

	#>
	[OutputType([void])]
	[CmdletBinding()]
	param
	(
		[Parameter(Mandatory)]
		[ValidateNotNullOrEmpty()]
		[string]$ComputerName,

		[Parameter(Mandatory)]
		[ValidateNotNullOrEmpty()]
		[string]$Name,

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[int]$Timeout = 3600, ## seconds

		[Parameter()]
		[ValidateNotNullOrEmpty()]
		[pscredential]$Credential
	)
	begin {
		$ErrorActionPreference = 'Stop'
	}
	process {
		try {
			$sessParams = @{
				ComputerName = $ComputerName
			}
			if ($PSBoundParameters.ContainsKey('Credential')) {
				$sessParams.Credential = $Credential
			}
			$session = New-PSSession @sessParams

			$scriptBlock = {
				$VerbosePreference = 'Continue'
				$timer = [Diagnostics.Stopwatch]::StartNew()
				while (((Get-ScheduledTask -TaskName $args[0]).State -ne 'Ready') -and ($timer.Elapsed.TotalSeconds -lt $args[1])) {
					Write-Verbose -Message "Waiting on scheduled task [$($args[0])]"
					Start-Sleep -Seconds 3
				}
				$timer.Stop()
				Write-Verbose -Message "We waited [$($timer.Elapsed.TotalSeconds)] seconds on the task [$($args[0])]"
			}

			Invoke-Command -Session $session -ScriptBlock $scriptBlock -ArgumentList $Name, $Timeout
		} catch {
			Write-Error -Message $_.Exception.Message
		} finally {
			if (Test-Path Variable:\session) {
				$session | Remove-PSSession
			}
		}
	}
}

Function Get-DiskCheck {
[cmdletbinding(DefaultParameterSetName = "name")]
Param( [Parameter(Position = 0, Mandatory, HelpMessage = "Enter a computer name to check", ParameterSetName = "name", ValueFromPipeline)] [Alias("cn")] [ValidateNotNullorEmpty()] [string[]]$Computername,
[Parameter(Mandatory, HelpMessage = "Enter the path to a text file of computer names", ParameterSetName = "file" )] [ValidateScript({ if (Test-Path $_) { $True } else { Throw "Cannot validate path $_" } })] [ValidatePattern("\.txt$")] [string]$Path,
[ValidateRange(10,50)] [int]$Threshhold = 25,
[ValidateSet("C:","D:","E:","F:")] [string]$Drive = "C:",
[switch]$Test )
Begin { Write-Verbose "[BEGIN ] Starting: $($MyInvocation.Mycommand)"
$cimParam = @{ Classname = "Win32_LogicalDisk" Filter = "DeviceID='$Drive'" Computername = $Null ErrorAction = "Stop" } } #begin
Process {
if ($PSCmdlet.ParameterSetName -eq 'name') { $names = $Computername } else { #get list of names and trim off any extra spaces Write-Verbose "[PROCESS] Importing names from $path" $names = Get-Content -Path $path | Where {$_ -match "\w+"} | foreach {$_.Trim()}
}
if ($test) { Write-Verbose "[PROCESS] Testing connectivity" #ignore errors for offline computers $names = $names | Where {Test-WSMan $_ -ErrorAction SilentlyContinue} }
foreach ($computer in $names) { $cimParam.Computername = $Computer Write-Verbose "[PROCESS] Querying $($computer.toUpper())" Try { $data = Get-Ciminstance @cimParam
#write custom result to the pipeline $data | Select PSComputername, DeviceID,Size,Freespace, @{Name="PctFree";Expression = {[math]::Round(($_.freespace/$_.size)*100,2)}}, @{Name="OK";Expression = { [int]$p = ($_.freespace/$_.size)*100 if ($p -ge $Threshhold) { $True } else { $false } }},@{Name="Date";Expression={(Get-Date)}} } Catch { Write-Warning "[$($computer.toUpper())] Failed. $($_.Exception.message)" } } #foreach computer
} #process
End { Write-Verbose "[END ] Ending: $($MyInvocation.Mycommand)" } #end
}
